package lecture_1;

public class test_3_assignment_operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=8;
		x +=3; 
		System.out.println(x);
		x -=3;
		System.out.println(x);
		x *=3;
		System.out.println(x);
		x /=3;
		System.out.println(x);
	}

}
